import React from 'react';
import { Link } from 'react-router-dom';
import Navbar from './Navbar';

const booksByCategory = {
  "Fiction": [
    { id: 1, title: "The Great Gatsby", author: "F. Scott Fitzgerald", price: 9.99, image: "https://covers.openlibrary.org/b/id/8225261-M.jpg", description: "A classic American novel about the Jazz Age and the American Dream." },
    { id: 2, title: "To Kill a Mockingbird", author: "Harper Lee", price: 12.99, image: "https://covers.openlibrary.org/b/id/8228691-M.jpg", description: "A gripping tale of racial injustice and childhood innocence in the American South." },
    { id: 5, title: "The Catcher in the Rye", author: "J.D. Salinger", price: 10.99, image: "https://covers.openlibrary.org/b/id/8231258-M.jpg", description: "A coming-of-age story following Holden Caulfield's journey through New York City." }
  ],
  "Dystopian": [
    { id: 3, title: "1984", author: "George Orwell", price: 11.99, image: "https://covers.openlibrary.org/b/id/7222246-M.jpg", description: "A chilling vision of a totalitarian future where Big Brother watches everything." }
  ],
  "Romance": [
    { id: 4, title: "Pride and Prejudice", author: "Jane Austen", price: 8.99, image: "https://covers.openlibrary.org/b/id/8091016-M.jpg", description: "A witty romance about Elizabeth Bennet and the proud Mr. Darcy." }
  ]
};

function Home({ addToCart, cart, user, setUser }) {
  return (
    <div>
      <Navbar cart={cart} user={user} setUser={setUser} />
      <div className="home-container">
        {Object.entries(booksByCategory).map(([category, books]) => (
          <div key={category} className="category-section">
            <h2>{category}</h2>
            <div className="books-grid">
              {books.map(book => (
                <div key={book.id} className="book-card">
                  <Link to={`/book/${book.id}`}>
                    <img src={book.image} alt={book.title} className="book-cover" />
                  </Link>
                  <h3><Link to={`/book/${book.id}`}>{book.title}</Link></h3>
                  <p>by {book.author}</p>
                  <p className="price">${book.price}</p>
                  <button onClick={() => addToCart(book)}>Add to Cart</button>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export { booksByCategory };
export default Home;
