import React from 'react';
import { Link } from 'react-router-dom';
import Navbar from './Navbar';

const books = [
  { id: 1, title: "The Great Gatsby", author: "F. Scott Fitzgerald", price: 9.99 },
  { id: 2, title: "To Kill a Mockingbird", author: "Harper Lee", price: 12.99 },
  { id: 3, title: "1984", author: "George Orwell", price: 11.99 },
  { id: 4, title: "Pride and Prejudice", author: "Jane Austen", price: 8.99 },
  { id: 5, title: "The Catcher in the Rye", author: "J.D. Salinger", price: 10.99 }
];

function Home({ addToCart, cart, user, setUser }) {
  return (
    <div>
      <Navbar cart={cart} user={user} setUser={setUser} />
      <div className="home-container">
        <h2>Featured Ebooks</h2>
        <div className="books-grid">
          {books.map(book => (
            <div key={book.id} className="book-card">
              <h3>{book.title}</h3>
              <p>by {book.author}</p>
              <p className="price">${book.price}</p>
              <button onClick={() => addToCart(book)}>Add to Cart</button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Home;
