import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './components/Login';
import Register from './components/Register';
import Home from './components/Home';
import Cart from './components/Cart';
import Checkout from './components/Checkout';
import BookDetail from './components/BookDetail';
import './App.css';

function App() {
  const [user, setUser] = useState(null);
  const [cart, setCart] = useState([]);

  const addToCart = (book) => {
    setCart([...cart, book]);
  };

  const removeFromCart = (bookId) => {
    setCart(cart.filter(book => book.id !== bookId));
  };

  return (
    <Router>
      <div className="App">
        <Routes>
          <Route path="/login" element={<Login setUser={setUser} />} />
          <Route path="/register" element={<Register />} />
          <Route path="/home" element={
            user ? <Home addToCart={addToCart} cart={cart} user={user} setUser={setUser} /> : <Navigate to="/login" />
          } />
          <Route path="/cart" element={
            user ? <Cart cart={cart} removeFromCart={removeFromCart} user={user} setUser={setUser} /> : <Navigate to="/login" />
          } />
          <Route path="/checkout" element={
            user ? <Checkout cart={cart} setCart={setCart} user={user} setUser={setUser} /> : <Navigate to="/login" />
          } />
          <Route path="/book/:id" element={
            user ? <BookDetail addToCart={addToCart} user={user} setUser={setUser} cart={cart} /> : <Navigate to="/login" />
          } />
          <Route path="/" element={<Navigate to="/login" />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
