import React, { useEffect, useState } from "react";
import axios from "axios";

const Orders = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  const token = localStorage.getItem("token"); // JWT saved on login

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/orders", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setOrders(res.data);
      } catch (err) {
        console.error(err);
      }
      setLoading(false);
    };

    fetchOrders();
  }, [token]);

  if (loading) return <h2>Loading ordersâ€¦</h2>;

  if (orders.length === 0)
    return <h2>No orders found. Start shopping ðŸ“š</h2>;

  return (
    <div style={{ padding: "20px" }}>
      <h1>Your Orders</h1>

      {orders.map((order) => (
        <div key={order._id} style={styles.card}>
          <p><strong>Order ID:</strong> {order._id}</p>
          <p><strong>Date:</strong> {new Date(order.createdAt).toLocaleString()}</p>

          <h3>Items:</h3>
          {order.items.map((item, index) => (
            <div key={index} style={styles.item}>
              <p>{item.title}</p>
              <p>â‚¹{item.price} Ã— {item.quantity}</p>
            </div>
          ))}

          <h3>Total: â‚¹{order.total}</h3>

          <p>
            <strong>Payment:</strong> {order.payment?.method || "N/A"} -{" "}
            <span
              style={{
                color: order.payment?.status === "Paid" ? "green" : "red",
              }}
            >
              {order.payment?.status}
            </span>
          </p>
        </div>
      ))}
    </div>
  );
};

const styles = {
  card: {
    border: "1px solid #ddd",
    padding: "15px",
    marginBottom: "20px",
    borderRadius: "8px",
    background: "#fff",
  },
  item: {
    display: "flex",
    justifyContent: "space-between",
  },
};

export default Orders;
